import React, { useState } from "react";
import "./ConfigForm.css";

const ConfigForm = () => {
  const [stocks, setStocks] = useState([]);
  const [capital, setCapital] = useState(0);
  const [support, setSupport] = useState(1);
  const [resistance, setResistance] = useState(1);
  const [isFirstTime, setIsFirstTime] = useState(true);

  const allStocks = ["Stock1", "Stock2", "Stock3", "Stock4", "Stock5"]; // Mock stocks

  const handleStockSelection = (stock) => {
    setStocks((prev) =>
      prev.includes(stock) ? prev.filter((s) => s !== stock) : [...prev, stock]
    );
    setCapital((prev) =>
      prev.includes(stock) ? prev - 10000 : prev + 10000
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log({ stocks, capital, support, resistance, isFirstTime });
  };

  return (
    <div className="config-container">
      <div className="config-box">
        <h2>Trading Configuration</h2>
        <form onSubmit={handleSubmit}>
          {/* Instrument Selection */}
          <div className="form-group horizontal-group">
            <h3>Select Instruments</h3>
            <label>
              <input type="radio" name="instruments" value="Top 50" /> All Top 50
              Stocks
            </label>
            <label>
              <input type="radio" name="instruments" value="Selective" />{" "}
              Selective Stocks
            </label>
            <label>
              <input type="radio" name="instruments" value="Shariah" /> Shariah
              Compliant
            </label>
          </div>

          {/* Stock Selection */}
          <div className="form-group">
            <h3>Select Stocks</h3>
            <div className="stock-selection">
              {allStocks.map((stock) => (
                <span
                  key={stock}
                  className={`stock-tag ${
                    stocks.includes(stock) ? "selected" : ""
                  }`}
                  onClick={() => handleStockSelection(stock)}
                >
                  {stock}
                </span>
              ))}
            </div>
          </div>

          {/* Capital Input */}
          <div className="form-group">
            <label>Total Capital (Minimum: {stocks.length * 10000})</label>
            <input
              type="range"
              min={stocks.length * 10000}
              max={1000000}
              value={capital}
              onChange={(e) => setCapital(e.target.value)}
            />
            <input
              type="number"
              value={capital}
              onChange={(e) => setCapital(e.target.value)}
            />
          </div>

          {/* Support and Resistance */}
          <div className="form-group">
            <label>Support % (1–10): {support}%</label>
            <input
              type="range"
              min="1"
              max="10"
              value={support}
              onChange={(e) => setSupport(e.target.value)}
            />
            <label>Resistance % (1–6): {resistance}%</label>
            <input
              type="range"
              min="1"
              max="6"
              value={resistance}
              onChange={(e) => setResistance(e.target.value)}
            />
          </div>

          {/* First Time Trading */}
          <div className="form-group horizontal-group">
            <label>Are you trading for the first time?</label>
            <label>
              <input
                type="radio"
                name="firstTime"
                value="Yes"
                onChange={() => setIsFirstTime(true)}
              />{" "}
              Yes
            </label>
            <label>
              <input
                type="radio"
                name="firstTime"
                value="No"
                onChange={() => setIsFirstTime(false)}
              />{" "}
              No
            </label>
          </div>

          <button type="submit" className="btn-config-save">
            Save Configuration
          </button>
        </form>
      </div>
    </div>
  );
};

export default ConfigForm;
