import React, { useState } from "react";
import "./ProfileForm.css";

const ProfileForm = () => {
  const [profile, setProfile] = useState({
    name: "",
    email: "",
    zerodhaKey: "",
    zerodhaSecret: "",
  });

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Profile Updated:", profile);
  };

  return (
    <form className="profile-form" onSubmit={handleSubmit}>
      <h2>Update Profile</h2>
      <label>Name</label>
      <input name="name" value={profile.name} onChange={handleChange} />
      <label>Email</label>
      <input name="email" value={profile.email} readOnly />
      <label>Zerodha API Key</label>
      <input name="zerodhaKey" value={profile.zerodhaKey} onChange={handleChange} />
      <label>Zerodha API Secret</label>
      <input name="zerodhaSecret" value={profile.zerodhaSecret} onChange={handleChange} />
      <button type="submit" className="btn">Save</button>
    </form>
  );
};

export default ProfileForm;
