import React, { useState } from "react";

const ConfigForm = () => {
  const [stocks, setStocks] = useState([]);
  const [capital, setCapital] = useState(0);
  const [support, setSupport] = useState(1);
  const [resistance, setResistance] = useState(1);
  const [isFirstTime, setIsFirstTime] = useState(true);

  const handleStockSelection = (selectedStocks) => {
    setStocks(selectedStocks);
    setCapital(selectedStocks.length * 10000);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Call API to save configuration
    console.log({ stocks, capital, support, resistance, isFirstTime });
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Trading Configuration</h2>
      
      {/* Instrument Selection */}
      <div>
        <h3>Select Instruments</h3>
        <input type="radio" name="instruments" value="Top 50" /> All Top 50 Stocks
        <input type="radio" name="instruments" value="Selective" /> Selective Stocks
        <input type="radio" name="instruments" value="Shariah" /> Shariah Compliant
      </div>

      {/* Stock Selection */}
      <div>
        <label>Select Stocks</label>
        <select multiple onChange={(e) => handleStockSelection([...e.target.selectedOptions].map(o => o.value))}>
          <option>Stock1</option>
          <option>Stock2</option>
          <option>Stock3</option>
        </select>
      </div>
      
      {/* Capital */}
      <div>
        <label>Total Capital (Minimum: {stocks.length * 10000})</label>
        <input type="range" min={stocks.length * 10000} max={1000000} value={capital} onChange={(e) => setCapital(e.target.value)} />
        <input type="number" value={capital} onChange={(e) => setCapital(e.target.value)} />
      </div>

      {/* Support and Resistance */}
      <div>
        <label>Support % (1–10)</label>
        <input type="range" min="1" max="10" value={support} onChange={(e) => setSupport(e.target.value)} />
        <label>Resistance % (1–6)</label>
        <input type="range" min="1" max="6" value={resistance} onChange={(e) => setResistance(e.target.value)} />
      </div>

      {/* Existing Trading */}
      <div>
        <label>Are you trading for the first time?</label>
        <input type="radio" name="firstTime" value="Yes" onChange={() => setIsFirstTime(true)} /> Yes
        <input type="radio" name="firstTime" value="No" onChange={() => setIsFirstTime(false)} /> No
      </div>

      <button type="submit">Save Configuration</button>
    </form>
  );
};

export default ConfigForm;
